<?phpdefined('BASEPATH') OR exit('No direct script access allowed');?><div style="border:1px solid #990000;padding-left:20px;margin:0 0 10px 0;">    <h4>An uncaught Exception was encountered</h4>    <p>Type: <?php echo get_class($exception); ?></p>    <p>Message: <?php echo $message; ?></p>    <p>Filename: <?php echo $exception->getFile(); ?></p>    <p>Line Number: <?php echo $exception->getLine(); ?></p>    <?php                 $ci =& get_instance();        $url = site_url( $ci->uri->uri_string() );        $user_id = getLoginUserData('user_id');                $build_body = "Message: {$message} <br/><br/>";        $build_body .= "Exception: ". get_class($exception) ."<br/><br/>";        $build_body .= "File: {$exception->getFile()}<br/><br/>";        $build_body .= "Line: {$exception->getLine()}<br/><br/>";        $build_body .= "URL: {$url}<br/><br/>";        $build_body .= "User: {$user_id}<br/><br/>";;        $build_body .= "Error Erporting File: error_general.php<br/><br/>";            $option['body'] =  $build_body;        Modules::run('mail/send_error_report_to_dev', $option );                    ?>        <?php if (defined('SHOW_DEBUG_BACKTRACE') && SHOW_DEBUG_BACKTRACE === TRUE): ?>        <p>Backtrace:</p>        <?php foreach ($exception->getTrace() as $error): ?>            <?php if (isset($error['file']) && strpos($error['file'], realpath(BASEPATH)) !== 0): ?>                <p style="margin-left:10px">                    File: <?php echo $error['file']; ?><br />                    Line: <?php echo $error['line']; ?><br />                    Function: <?php echo $error['function']; ?>                </p>            <?php endif ?>        <?php endforeach ?>    <?php endif ?></div>