<?phpdefined('BASEPATH') OR exit('No direct script access allowed');?><div style="border:1px solid #990000;padding-left:20px;margin:0 0 10px 0;">    <h4>A PHP Error was encountered</h4>    <p>Severity: <?php echo $severity; ?></p>    <p>Message:  <?php echo $message; ?></p>    <p>Filename: <?php echo $filepath; ?></p>    <p>Line Number: <?php echo $line; ?></p>    <?php           $ci =& get_instance();        $url = site_url( $ci->uri->uri_string() );        $user_id = getLoginUserData('user_id');                $build_body = "File: {$filepath}<br/><br/>";        $build_body .= "Line: {$line}<br/><br/>";        $build_body .= "Message: {$message} <br/><br/>";        $build_body .= "Severity: {$severity}<br/><br/>";                $build_body .= "URL: {$url}<br/><br/>";        $build_body .= "User: {$user_id}<br/><br/>";        $build_body .= "Error Erporting File: error_php.php<br/><br/>";            $option['body'] =  $build_body;        Modules::run('mail/send_error_report_to_dev', $option );                    ?>        <?php if (defined('SHOW_DEBUG_BACKTRACE') && SHOW_DEBUG_BACKTRACE === TRUE): ?>        <p>Backtrace:</p>        <?php foreach (debug_backtrace() as $error): ?>            <?php if (isset($error['file']) && strpos($error['file'], realpath(BASEPATH)) !== 0): ?>                <p style="margin-left:10px">                    File: <?php echo $error['file'] ?><br />                    Line: <?php echo $error['line'] ?><br />                    Function: <?php echo $error['function'] ?>                </p>            <?php endif ?>        <?php endforeach ?>    <?php endif ?></div>